@mixin liststyl {
    color: #e5e7eb;
    border: 2px solid transparent;
    border-radius: 6px;
    padding: 0.6em;
}

@mixin hover {
    border: 2px solid white;
    background-color: rgba(74, 70, 70, 0.756);
    animation-duration: 500ms;
}

html {
    padding: 0;
    margin: 0;
    min-height: 100%;
    box-sizing: border-box;
    border-width: 0;
    border-style: solid;
    border-color: #e5e7eb;

    body {
        font-family: 'Courier New', Courier, monospace;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        background-image: url(../assets/img/wp4094503.jpg);
        background-size: cover;
        background-repeat: no-repeat;
        margin: 0;

        #_news_letter {
            width: 99.7%;
            display: flex;
            justify-content: center;
            border: 0;
            align-items: center;
            background-color: #fff;
            height: 15vh;
            padding: 2px;

            form {
                width: 90%;
                height: 90%;
                display: flex;
                justify-content: center;
                align-items: center;

                #_email {
                    width: 65%;
                    height: 3em;
                    margin-right: 1em;
                    border-radius: 6px;
                    outline: none;
                    border: 0;
                    background-color: rgb(216, 211, 211);
                }

                #_email:hover {
                    background-color: #f1f1f1;
                }

                #_email::placeholder {
                    padding-left: 2px;
                }

                #_ok {
                    width: 10em;
                    height: 3.2em;
                    border: 0;
                    border-radius: 6px;
                    background-color: rgb(124, 231, 71);
                }

                #_ok:hover {
                    cursor: pointer;
                    background-color: rgb(103, 182, 63);
                    box-shadow: black 0px 0px 20px 0px;
                    transform: translateY(-5px);
                }
            }
        }
    }
}

li {
    list-style: none;
}

a {
    text-decoration: none;
}

header {
    position: sticky;
    top: -95px;
    z-index: 50;
    width: 99%;
    backdrop-filter: blur(5px);
    display: flex;
    flex-direction: column;
    justify-content: center;
    box-shadow: inset 0px -10px 10px -10px rgba(0, 0, 0, 0.75);
    padding: 5px;

    #logo {
        // border: 1px solid black;
        width: 70%;
        margin: auto;
        height: 4em;
        padding: 3px;
        margin-top: 8px;
        display: flex;
        align-items: center;
        justify-content: center;

        a {
            height: 100%;
            // border: 1px solid black;
            width: 30%;
            display: flex;
            justify-content: center;

            img {
                // border: 1px solid black;
                width: 100%;
                background-size: contain;
                height: 100%;

            }
        }

        h3 {
            margin-left: 30px;
            font-family: cursive;
            color: greenyellow;
        }
    }

    nav {
        width: 100%;
        margin-top: 0;
        padding: 2px;
        // position: fixed;
        top: 0;

        ol {
            display: flex;
            height: 3em;
            align-items: center;
            justify-content: center;

            li {
                margin-left: 1.5em;

                a {
                    @include liststyl;
                }

                a:hover {
                    @include hover;
                }

            }

            #action {
                margin-left: 5em;
                display: flex;
                padding: 3px;
                align-items: center;
                justify-content: space-between;
                float: right;

                a {
                    margin-left: 0.5em;
                    @include liststyl;
                }

                a:hover {
                    @include hover;
                }
            }
        }

    }

    nav:hover {
        backdrop-filter: blur(330em);
    }
        .navbar {
            // position:fixed;
            margin-top: 0;
            height: 3.5em;
            z-index: 50;
            backdrop-filter: blur(120px);
        }

}


main {
    margin-top: 25vh;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    #section1 {
        width: 80vw;
        padding: 1em;
        margin: auto;
        // border: red 1px solid;
        display: grid;
        gap: 7em;
        align-items: center;
        justify-content: center;
        grid-template-columns: repeat(auto-fill, 100px);
        grid-auto-columns: repeat(3, minmax(0, 1fr));

        a {
            width: 140px;
            height: 140px;
            padding: 0.5em;
            margin: auto;
            border: 1px solid black;
            display: flex;
            flex-direction: column;
            justify-content: center;
            backdrop-filter: blur(20px);
            border-radius: 10px;

            img {
                width: 90%;
                height: 70%;
                margin-left: 5px;
                margin-top: 5px;
            }

            h3 {
                width: 90%;
                margin: auto;
                text-align: center;
            }
        }

        a:hover {
            border: 1px solid white;
            color: black;
            backdrop-filter: blur(10px);
            transform: translateY(-10px);
            scale: 1.1;
            box-shadow: 0px 0px 20px 0px black;

            h3 {
                color: #e5e7eb;
            }
        }
    }
    #section2_{
        height: 3em;
    }

    #section2 {
        width: 97vw;
        margin: auto;
        margin-top: 30vh;
        margin-bottom: 10vh;
        padding: 5px;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        border-radius: 10px;
        // background-color: rgb(227, 33, 33);
        backdrop-filter: blur(40px);

        #thti {
            width: 80vw;
            height: 1.5em;
            margin: auto;
            text-align: center;
            padding: 3em;

            h1 {
                color: rgb(25, 195, 25);
                font-family: cursive;
                font-weight: 500;
            }

            h1:hover {
                scale: 1.1;
                font-weight: bolder;
            }
        }

        #_thti_storie {
            width: 90%;
            margin: auto;
            margin-top: 5vh;
        }
    }

    #section2:hover {
        backdrop-filter: blur(15px);
        box-shadow: inset 0px 0px 20px 0px rgb(13, 165, 23);
        color: white;
    }
}

