// <a href='https://pngtree.com/so/creative'>creative png from pngtree.com/</a>
footer {
    height: 50vh;
    width: 100%;
    border-top: 2px solid black;
    background-color: rgb(76, 69, 69);
    display: flex;
    justify-content: center;
    align-items: center;

    #pied1,
    #pied2 {
        width: 30vw;
        display: flex;
        justify-content: center;
    }

    #pied_centre {
        flex-grow: 1;
        display: flex;
        justify-content: center;
        align-items: center;
        width: 40vw;
        height: 85%;

        ol {
            width: 80%;
            height: 80%;
            margin: auto;
            display: flex;
            justify-content: center;
            align-items: center;

            li {
                width: calc(100%/4);
                margin-right: 2em;
                display: flex;
                justify-content: space-around;
                align-items: center;
                padding: 1px;

                a {
                    width: 100%;
                    height: 100%;

                    img {
                        width: 40px;
                        height: 40px;
                        margin: auto;
                    }
                }
            }
        }
    }

}

