@mixin liststyl {
    color: #e5e7eb;
    border: 2px solid transparent;
    border-radius: 6px;
    padding: 0.6em;
}

@mixin hover {
    border: 2px solid white;
    background-color: rgba(74, 70, 70, 0.756);
    animation-duration: 500ms;
}

html {
    padding: 0;
    margin: 0;
    min-height: 100%;
    box-sizing: border-box;
    border-width: 0;
    border-style: solid;
    border-color: #e5e7eb;

    body {
        font-family: 'Courier New', Courier, monospace;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        background-image: url(../assets/img/wp4094503.jpg);
        background-size: cover;
        background-repeat: no-repeat;
        margin: 0;
    }
}

li {
    list-style: none;
}

a {
    text-decoration: none;
}

header {
    width: 100%;
    // height: 8em;
    backdrop-filter: blur(5px);
    // background-color: green;
    display: flex;
    flex-direction: column;
    justify-content: center;
    box-shadow: inset 0px -10px 10px -10px rgba(0, 0, 0, 0.75);
    padding: 5px;

    #logo {
        border: 1px solid black;
        width: 70%;
        margin: auto;
        height: 4em;
        padding: 3px;
        margin-top: 8px;
        display: flex;
        align-items: center;
        justify-content: center;

        a {
            height: 100%;
            border: 1px solid black;
            width: 30%;
            display: flex;
            justify-content: center;

            img {
                border: 1px solid black;
                width: 100%;
                background-size: contain;
                height: 100%;

            }
        }

        h3 {
            margin-left: 30px;
            font-family: cursive;
            color: greenyellow;
        }
    }

    nav {
        width: 100%;
        margin-top: 0.8em;
        padding: 2px;

        ol {
            display: flex;
            height: 3em;
            align-items: center;
            justify-content: center;

            li {
                margin-left: 1.5em;

                a {
                    @include liststyl;
                }

                a:hover {
                    @include hover;
                }

            }

            #action {
                margin-left: 5em;
                display: flex;
                padding: 3px;
                align-items: center;
                justify-content: space-between;
                float: right;

                a {
                    margin-left: 0.5em;
                    @include liststyl;
                }

                a:hover {
                    @include hover;
                }
            }
        }
    }

    nav:hover {
        backdrop-filter: blur(30px);
    }

}

main {
    margin-top: 25vh;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

    #section1 {
        width: 80vw;
        padding: 1em;
        margin: auto;
        // border: red 1px solid;
        display: grid;
        gap: 7em;
        align-items: center;
        justify-content: center;
        grid-template-columns: repeat(auto-fill, 100px);
        grid-auto-columns: repeat(3, minmax(0, 1fr));

        a {
            width: 140px;
            height: 140px;
            padding: 0.5em;
            margin: auto;
            border: 1px solid black;
            display: flex;
            flex-direction: column;
            justify-content: center;
            backdrop-filter: blur(20px);
            border-radius: 10px;
            img{
                width: 90%;
                height: 70%;
                margin-left: 5px;
                margin-top: 5px;
            }
            h3{
                width: 90%;
                margin: auto;
                text-align: center;
            }
        }

        a:hover {
            border: 1px solid white;
            color: black;
            backdrop-filter: blur(10px);
            transform: translateY(-10px);
            scale: 1.1;
            box-shadow: 0px 0px 20px 0px black;
            h3{
                color: #e5e7eb;
            }
        }
    }
    #section2{
        width: 80vw;
        margin: auto;
        margin-top: 30vh;
        margin-bottom: 10vh;
        padding: 5px;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        border-radius: 10px;
        // background-color: rgb(227, 33, 33);
        backdrop-filter: blur(40px);
        #thti{
            width: 80vw;
            height: 1.5em;
            margin: auto;
            text-align: center;
            padding: 3em;
            h1{
                color: rgb(25, 195, 25);
                font-family: cursive;
                font-weight: 500;
            }
            h1:hover{
                scale: 1.1;
                font-weight: bolder;
            }
        }
        #thti_storie{
            width: 90%;
            margin: auto;
            margin-top: 5vh;
        }
    }
    #section2:hover{
        backdrop-filter: blur(15px);
        color: white;
    }
}
